import React, { useState, useEffect } from 'react';
import style from './Video.module.css';

const Video = () => {
  const [isVideo2Visible, setIsVideo2Visible] = useState(false);
  const [opacity, setOpacity] = useState(1);

  const backgroundChange = () => {
    setIsVideo2Visible(true);
  };

  useEffect(() => {
    if (isVideo2Visible) {
      const timeout = setTimeout(() => {
        setOpacity(0);
        setTimeout(() => {
          setOpacity(1);
          setIsVideo2Visible(false);
        }, 500); // 0.5초 후에 첫 번째 동영상으로 전환
      }, 1000); // 1초 후에 투명도를 0으로 조정
      return () => clearTimeout(timeout);
    }
  }, [isVideo2Visible]);

  return (
    <div className="video-container">
      {isVideo2Visible ? (
        <video
          className={`${style.video} ${style.transition}`}
          src="/videos/main2.mp4"
          autoPlay
          loop
          muted
          style={{ opacity }}
        />
      ) : (
        <video
          className={style.video}
          onClick={backgroundChange}
          src="/videos/main.mp4"
          autoPlay
          loop
          muted
        />
      )}
    </div>
  );
};

export default Video;
