import React, { useEffect, useState } from 'react';
import style from './Category_container.module.css'

function Category_container({children, delay, textChanged}) {
    const [startAnimation, setStartAnimation] = useState(false);
    const [runAnimation, setRunAnimation] = useState(false);

    useEffect(() => {
        if (textChanged === false) {
            setRunAnimation(true);
        }
    }, [textChanged]);

    useEffect(() => {
        let timer;
        if (runAnimation) {
            setStartAnimation(true);
            timer = setTimeout(() => {
                setStartAnimation(false);
                setRunAnimation(false);
            }, 1000 + delay * 1000);
        }
        return () => clearTimeout(timer);
    }, [runAnimation, delay]);

    return (
        <div className={`${style.container} ${startAnimation ? '' : style.noAnimation}`} style={{animationDelay: `${delay}s`}}>
            <div className={style.container__line}>
                <div className={style.container__line__text}>
                    {children}
                </div>
            </div>
        </div>
    );
}

export default Category_container;
