import React, { useState } from 'react';
import style from './Video.module.css';

const Video = ({musicStart}) => {
  const [isVideo2Visible, setIsVideo2Visible] = useState(false);

  const backgroundChange = () => {
    setIsVideo2Visible(true);
  };

  const handleMusic = () => {
    // onChanged 이벤트 핸들러 실행
    if (typeof musicStart === 'function') {
      musicStart();
    }
  }


  return (
    <div className={style.video_container}>
      {isVideo2Visible ? (
        <video
          className={`${style.video} ${style.transfer}`}
          src="/videos/main2.mp4"
          autoPlay
          loop
          muted
        />
      ) : (
        <video
          onMouseOver={handleMusic}
          className={style.video}
          onClick={backgroundChange}
          src="/videos/main.mp4"
          autoPlay
          loop
          muted
        />
      )}
    </div>
  );
};
export default Video;
