import React, { useState } from 'react';
import style from './Video.module.css';
import Music from './Music';

const Video = () => {
  const [isVideo2Visible, setIsVideo2Visible] = useState(false);
  const musicRef = React.useRef(null);

  const handlePlayVideo = () => {
    if (musicRef.current) {
      musicRef.current.onPlayVideo();
      console.log('efefe')
    }
  };

  const backgroundChange = () => {
    setIsVideo2Visible(true);
  };

  return (
    <div className="video-container">
      {/* Music 컴포넌트 렌더링 */}
      <Music ref={musicRef} />

      {isVideo2Visible ? (
        <video
          className={`${style.video} ${style.transfer}`}
          src="/videos/main2.mp4"
          autoPlay
          loop
          muted
        />
      ) : (
        <video
          onMouseOver={handlePlayVideo}
          className={style.video}
          onClick={backgroundChange}
          src="/videos/main.mp4"
          autoPlay
          loop
          muted
        />
      )}
    </div>
  );
};

export default Video;
