import React, { useState } from 'react';
import style from './Video.module.css';

const Video = ({ musicStart, afterClick, after2Click }) => {
  const [isVideo2Visible, setIsVideo2Visible] = useState(false);
  const [SizeVideo, setSizeVideo] = useState(false);
  const [newVideoContain, setNewVideoContainer] = useState(false);

  const operation = () => {
    setIsVideo2Visible(true);
    musicStart();
    afterClick();
  };

  const operation2 = () => {
    setNewVideoContainer(!newVideoContain);
    setSizeVideo(!SizeVideo);
    after2Click();
  };

  const sizeProcess = SizeVideo ? 'reduced_container' : 'normal_container';
  const newVideoContainer = newVideoContain ? 'new_video_container' : '';

  return (
    <div className={newVideoContainer ? style.new_video_container : style.video_container}>
      {isVideo2Visible ? (
        <video
          className={`${style.video} ${style.transfer} ${style[sizeProcess]}`}
          onClick={operation2}
          src="/videos/main2.mp4"
          autoPlay
          loop
          muted
        />
      ) : (
        <video
          className={style.video}
          onClick={operation}
          src="/videos/astronaut.mp4"
          autoPlay
          loop
          muted
        />
      )}
    </div>
  );
};

export default Video;
