import React, { useState, useEffect } from 'react';
import style from './Video.module.css';

const Video = ({ musicStart, afterClick, after2Click, isTitleVisible }) => {
  const [isVideo2Visible, setIsVideo2Visible] = useState(false);
  const [SizeVideo, setSizeVideo] = useState(false);
  const [newVideoContain, setNewVideoContainer] = useState(false);
  const [replaceClass, setReplaceClass] = useState(false);
  const [isInitialChange, setIsInitialChange] = useState(false);

  const operation = () => {
    setIsVideo2Visible(true);
    musicStart();
    afterClick();
  };

  const operation2 = () => {
    setNewVideoContainer(!newVideoContain);
    setSizeVideo(!SizeVideo);
    after2Click();
  };

  useEffect(() => {
    if (isInitialChange && !newVideoContain) {
      setReplaceClass(true);
    }
  }, [newVideoContain]);

  useEffect(() => {
    setIsInitialChange(true);
  }, []);

  const containerClassName = replaceClass ? style.new_new : style.new_video_container;

  return (
    <div className={containerClassName}>
      {isVideo2Visible ? (
        <video
          className={`${style.transfer} ${style.video} ${style[sizeProcess]}`}
          onMouseEnter={operation2}
          onMouseLeave={operation2}
          src="/videos/main2.mp4"
          autoPlay
          loop
          muted
        />
      ) : (
        <video
          className={style.video}
          onClick={operation}
          src="/videos/astronaut.mp4"
          autoPlay
          loop
          muted
        />
      )}
    </div>
  );
};

export default Video;
