import React, { useState, useRef } from 'react';
import '../../App.css';
import './Main.module.css';
import About from './mainPages/About';

import Title from '../Title'
import Video from '../Video'
import Music from '../Music'
import Category from '../Category'
import { Outlet } from 'react-router-dom';
function Main() {
  const videoCode = 'U3u4pQ4WKOk'
  // const [musicSts, setMusicSt] = useState(null);

  const musicRef = useRef(null);

  const [isVisible, setisible] = useState(true);
  const [textChanged, setTextChanged] = useState(true);
  const [TitleVisible, setTitleVisible] = useState(false);
  const [reRenderVideo, setReRenderVideo] = useState(false);

  const handleMusicStart = () => {
    if (musicRef.current) {
      musicRef.current.onPlayVideo();
    }
  };

  const isTitleVisible= () =>{
    setTitleVisible(true);
  }

  const deleteText = () => {
      setisible(!isVisible);
  };

  const textChangeds = (element)=> {
    if(element=='false'){
      setTextChanged(false);
      setReRenderVideo(false)
      return
    }
    else if(element=='true'){
      setTextChanged(false);
      setReRenderVideo(true)
      return
    }
    setTextChanged(!textChanged);
  }

    return (
        <div>
          <Title TitleVisible={TitleVisible} />
          <Video
            isTitleVisible={isTitleVisible}
            afterClick={deleteText}
            after2Click={textChangeds}
            reRenderVideo={reRenderVideo}
            musicStart={handleMusicStart}
          />
          <Music ref={musicRef} videoId={videoCode} />
          <Category textChanged={textChanged} />

          <Outlet />
        </div>
      );
    }



  export default Main;
