import React, { useEffect, useState } from 'react';
import style from './Category_container.module.css'

function Category_container({children, delay, textChanged}) {
    const [startAnimation, setStartAnimation] = useState(textChanged);

    useEffect(() => {
        if (!textChanged) {
            setStartAnimation(true);
        }
    }, [textChanged]);

    return (
        <div className={`${style.container} ${startAnimation ? style.noAnimation : ''}`} style={{animationDelay: `${delay}s`}}>
            <div className={style.container__line}>
                <div className={style.container__line__text}>
                    {children}
                </div>
            </div>
        </div>
    );
}

export default Category_container;
