import React, { useState, useEffect } from 'react';
import style from './Video.module.css';

const Video = ({ musicStart, afterClick, after2Click, isTitleVisible }) => {
  const [isVideo2Visible, setIsVideo2Visible] = useState(false);
  const [SizeVideo, setSizeVideo] = useState(false);
  const [newVideoContain, setNewVideoContainer] = useState(false);
  const [isInitialRender, setIsInitialRender] = useState(true);

  const operation = () => {
    setIsVideo2Visible(true);
    musicStart();
    afterClick();
  };

  const operation2 = () => {
    setNewVideoContainer(!newVideoContain);
    setSizeVideo(!SizeVideo);
    after2Click();
  };

  useEffect(() => {
    if (isInitialRender) {
      setIsInitialRender(false);
    } else {
      document.getElementById('videoContainer').classList.remove(style.video_container);
      document.getElementById('videoContainer').classList.add(style.new_new);
    }
  }, [newVideoContain]);

  if (SizeVideo) {
    isTitleVisible();
  }

  return (
    <div id="videoContainer" className={style.video_container}>
      {isVideo2Visible ? (
        <video
          className={`${style.transfer} ${style.video}`}
          onMouseEnter={operation2}
          onMouseLeave={operation2}
          src="/videos/main2.mp4"
          autoPlay
          loop
          muted
        />
      ) : (
        <video
          className={style.video}
          onClick={operation}
          src="/videos/astronaut.mp4"
          autoPlay
          loop
          muted
        />
      )}
    </div>
  );
};

export default Video;
