import React, { useState } from 'react';
import style from './Video.module.css';

const Video = () => {
  const [isVideo2Visible, setIsVideo2Visible] = useState(false);

  const backgroundChange = () => {
    setIsVideo2Visible(true);
  };

  return (
    <div className="video-container">
      {isVideo2Visible ? (
        <video className={style.video} src="/videos/main2.mp4" autoPlay loop muted />
      ) : (
        <video className={style.video} onClick={backgroundChange} src="/videos/main.mp4" autoPlay loop muted />
      )}
    </div>
  );
};

export default Video;
